mapper.select:
select sc.id as id,su.name as subjectName,p.name as paperName, sc.commit_time as commitTime,sc.evaluation_time as evaluationTime,sc.score as score from t_score as sc,t_subject as su,t_paper as p where sc.user_id=#{userId} and sc.subject_id=su.id and sc.paper_id=p.id
and (sc.id like '%${searchText}%' or su.name like '%${searchText}%' or p.name like '%${searchText}%' or sc.commit_time like '%${searchText}%' or sc.evaluation_time like '%${searchText}%' or sc.score like '%${searchText}%')
order by sc.commit_time ${order}
limit ${start},${size}

select count(sc.id) as count from t_score as sc,t_subject as su,t_paper as p where sc.user_id=#{userId} and sc.subject_id=su.id and sc.paper_id=p.id
and (sc.id like binary '%${searchText}%' or su.name like binary '%${searchText}%' or p.name like binary '%${searchText}%' or sc.commit_time like binary '%${searchText}%' or sc.evaluation_time like binary '%${searchText}%' or sc.score like binary '%${searchText}%')
order by sc.commit_time ${order}

select id,account,name from sys_user
where id like binary '%${searchText}%' or name like binary '%${searchText}%' or account like binary '%${searchText}%'
order by name ${order}
limit ${start},${size}

select count(id) as count from sys_user
where id like binary '%${searchText}%' or name like binary '%${searchText}%' or account like binary '%${searchText}%'
order by name ${order}
